export default AmplitudeContextProvider;","language":"javascript","caption":""},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Then, in your root layout.js import the context provider and wrap your application.","type":"extended-text","version":1}],"direction":"ltr","format":"","indent":0,"type":"paragraph","version":1},{"type":"codeblock","version":1,"code":"//...Other imports
